import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertWalletSchema, insertLaunchPlanSchema, insertBundleExecutionSchema, insertActivitySchema, insertSystemMetricsSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Wallet routes
  app.get("/api/wallets", async (req, res) => {
    try {
      const wallets = await storage.getWallets();
      res.json(wallets);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch wallets" });
    }
  });

  app.get("/api/wallets/:id", async (req, res) => {
    try {
      const wallet = await storage.getWallet(req.params.id);
      if (!wallet) {
        return res.status(404).json({ message: "Wallet not found" });
      }
      res.json(wallet);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch wallet" });
    }
  });

  app.post("/api/wallets", async (req, res) => {
    try {
      const validatedData = insertWalletSchema.parse(req.body);
      const wallet = await storage.createWallet(validatedData);

      // Create activity for wallet generation
      await storage.createActivity({
        type: "wallet_generated",
        description: `Wallet ${wallet.label || wallet.id} generated`,
        walletId: wallet.id,
        status: "confirmed",
      });

      res.status(201).json(wallet);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid wallet data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create wallet" });
    }
  });

  app.post("/api/wallets/bulk", async (req, res) => {
    try {
      const { count, initialBalance, labelPrefix } = req.body;
      const wallets = [];

      for (let i = 0; i < count; i++) {
        // Generate mock wallet data - in production this would use ethers.js
        const walletData = {
          address: `0x${Math.random().toString(16).substring(2, 42)}`,
          privateKey: `0x${Math.random().toString(16).substring(2, 66)}`,
          publicKey: `0x${Math.random().toString(16).substring(2, 130)}`,
          balance: initialBalance || "0",
          status: "idle",
          label: `${labelPrefix || "Wallet"} #${(i + 1).toString().padStart(3, "0")}`,
        };

        const wallet = await storage.createWallet(walletData);
        wallets.push(wallet);
      }

      // Create bulk activity
      await storage.createActivity({
        type: "bulk_wallet_generation",
        description: `Generated ${count} wallets`,
        status: "confirmed",
      });

      res.status(201).json(wallets);
    } catch (error) {
      res.status(500).json({ message: "Failed to create wallets" });
    }
  });

  app.patch("/api/wallets/:id", async (req, res) => {
    try {
      const wallet = await storage.updateWallet(req.params.id, req.body);
      if (!wallet) {
        return res.status(404).json({ message: "Wallet not found" });
      }
      res.json(wallet);
    } catch (error) {
      res.status(500).json({ message: "Failed to update wallet" });
    }
  });

  app.delete("/api/wallets/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteWallet(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Wallet not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete wallet" });
    }
  });

  // Launch Plan routes
  app.get("/api/launch-plans", async (req, res) => {
    try {
      const plans = await storage.getLaunchPlans();
      res.json(plans);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch launch plans" });
    }
  });

  app.post("/api/launch-plans", async (req, res) => {
    try {
      const validatedData = insertLaunchPlanSchema.parse(req.body);
      const plan = await storage.createLaunchPlan(validatedData);
      res.status(201).json(plan);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid launch plan data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create launch plan" });
    }
  });

  // Bundle Execution routes
  app.get("/api/bundle-executions", async (req, res) => {
    try {
      const executions = await storage.getBundleExecutions();
      res.json(executions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch bundle executions" });
    }
  });

  app.post("/api/bundle-executions", async (req, res) => {
    try {
      const validatedData = insertBundleExecutionSchema.parse(req.body);
      const execution = await storage.createBundleExecution(validatedData);

      // Create activity for bundle execution
      await storage.createActivity({
        type: "bundle_execution",
        description: `Bundle execution started for ${execution.totalWallets} wallets`,
        status: "pending",
      });

      res.status(201).json(execution);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid bundle execution data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create bundle execution" });
    }
  });

  // Activity routes
  app.get("/api/activities", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 50;
      const activities = await storage.getActivities(limit);
      res.json(activities);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch activities" });
    }
  });

  // System Metrics routes
  app.get("/api/system-metrics", async (req, res) => {
    try {
      let metrics = await storage.getLatestSystemMetrics();

      // If no metrics exist, create default metrics
      if (!metrics) {
        const defaultMetrics = {
          latency: Math.floor(15 + Math.random() * 20),
          gasPrice: (5.0 + Math.random() * 3.0).toFixed(1),
          successRate: (95.0 + Math.random() * 5.0).toFixed(1),
          taxCollected: "0.000",
          cpuUsage: Math.floor(25 + Math.random() * 50),
          memoryUsage: Math.floor(40 + Math.random() * 40),
        };

        metrics = await storage.createSystemMetrics(defaultMetrics);
      }

      res.json(metrics);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch system metrics" });
    }
  });

  app.post("/api/system-metrics", async (req, res) => {
    try {
      const validatedData = insertSystemMetricsSchema.parse(req.body);
      const metrics = await storage.createSystemMetrics(validatedData);
      res.status(201).json(metrics);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid system metrics data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create system metrics" });
    }
  });

  // Stealth Funding endpoint
  app.post("/api/stealth-funding", async (req, res) => {
    try {
      const { source, method, totalAmount, selectedWallets } = req.body;

      // Get wallets to fund (either selected ones or all idle wallets)
      const wallets = selectedWallets && selectedWallets.length > 0 
        ? await Promise.all(selectedWallets.map((id: string) => storage.getWallet(id)))
        : await storage.getWalletsByStatus("idle");

      const validWallets = wallets.filter(Boolean);
      if (validWallets.length === 0) {
        return res.status(400).json({ message: "No wallets available for funding" });
      }

      const amount = parseFloat(totalAmount);
      const amountPerWallet = amount / validWallets.length;
      const taxRate = 0.05; // 5% tax collection
      const taxCollected = amount * taxRate;
      const netAmount = amount - taxCollected;
      const netAmountPerWallet = netAmount / validWallets.length;

      // Update wallet balances and status
      const fundedWallets = [];
      for (const wallet of validWallets) {
        const currentBalance = parseFloat(wallet.balance);
        const newBalance = currentBalance + netAmountPerWallet;

        const updatedWallet = await storage.updateWallet(wallet.id, {
          balance: newBalance.toFixed(8),
          status: "active",
          lastActivity: new Date(),
        });

        if (updatedWallet) {
          fundedWallets.push(updatedWallet);
        }
      }

      // Create activity for stealth funding
      await storage.createActivity({
        type: "stealth_funding",
        description: `Stealth funding executed: ${netAmount.toFixed(4)} BNB distributed to ${fundedWallets.length} wallets (${method} method)`,
        status: "confirmed",
        amount: netAmount.toFixed(8),
      });

      // Update system metrics with tax collection
      const currentMetrics = await storage.getLatestSystemMetrics();
      if (currentMetrics) {
        const newTaxCollected = parseFloat(currentMetrics.taxCollected) + taxCollected;
        await storage.createSystemMetrics({
          latency: currentMetrics.latency,
          gasPrice: currentMetrics.gasPrice,
          successRate: "98.5", // Slightly higher after successful funding
          taxCollected: newTaxCollected.toFixed(8),
          cpuUsage: currentMetrics.cpuUsage,
          memoryUsage: currentMetrics.memoryUsage,
        });
      }

      res.json({
        success: true,
        message: `Successfully funded ${fundedWallets.length} wallets`,
        totalAmount: amount,
        netAmount: netAmount,
        taxCollected: taxCollected,
        walletsUpdated: fundedWallets.length,
        method: method,
        source: source,
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to execute stealth funding" });
    }
  });

  // Statistics endpoint
  app.get("/api/stats", async (req, res) => {
    try {
      const wallets = await storage.getWallets();
      const activeWallets = await storage.getWalletsByStatus("active");
      const totalBalance = wallets.reduce((sum, wallet) => sum + parseFloat(wallet.balance), 0);

      res.json({
        totalWallets: wallets.length,
        activeWallets: activeWallets.length,
        totalBalance: totalBalance.toFixed(8),
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch statistics" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type Wallet, type InsertWallet, type LaunchPlan, type InsertLaunchPlan, type BundleExecution, type InsertBundleExecution, type Activity, type InsertActivity, type SystemMetrics, type InsertSystemMetrics } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Wallet methods
  getWallet(id: string): Promise<Wallet | undefined>;
  getWallets(): Promise<Wallet[]>;
  getWalletsByStatus(status: string): Promise<Wallet[]>;
  createWallet(wallet: InsertWallet): Promise<Wallet>;
  updateWallet(id: string, updates: Partial<Wallet>): Promise<Wallet | undefined>;
  deleteWallet(id: string): Promise<boolean>;

  // Launch Plan methods
  getLaunchPlan(id: string): Promise<LaunchPlan | undefined>;
  getLaunchPlans(): Promise<LaunchPlan[]>;
  createLaunchPlan(plan: InsertLaunchPlan): Promise<LaunchPlan>;
  updateLaunchPlan(id: string, updates: Partial<LaunchPlan>): Promise<LaunchPlan | undefined>;
  deleteLaunchPlan(id: string): Promise<boolean>;

  // Bundle Execution methods
  getBundleExecution(id: string): Promise<BundleExecution | undefined>;
  getBundleExecutions(): Promise<BundleExecution[]>;
  createBundleExecution(execution: InsertBundleExecution): Promise<BundleExecution>;
  updateBundleExecution(id: string, updates: Partial<BundleExecution>): Promise<BundleExecution | undefined>;

  // Activity methods
  getActivities(limit?: number): Promise<Activity[]>;
  createActivity(activity: InsertActivity): Promise<Activity>;

  // System Metrics methods
  getLatestSystemMetrics(): Promise<SystemMetrics | undefined>;
  createSystemMetrics(metrics: InsertSystemMetrics): Promise<SystemMetrics>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private wallets: Map<string, Wallet>;
  private launchPlans: Map<string, LaunchPlan>;
  private bundleExecutions: Map<string, BundleExecution>;
  private activities: Activity[];
  private systemMetrics: SystemMetrics[];

  constructor() {
    this.users = new Map();
    this.wallets = new Map();
    this.launchPlans = new Map();
    this.bundleExecutions = new Map();
    this.activities = [];
    this.systemMetrics = [];
  }

  // User methods
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  // Wallet methods
  async getWallet(id: string): Promise<Wallet | undefined> {
    return this.wallets.get(id);
  }

  async getWallets(): Promise<Wallet[]> {
    return Array.from(this.wallets.values()).sort((a, b) => 
      new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
    );
  }

  async getWalletsByStatus(status: string): Promise<Wallet[]> {
    return Array.from(this.wallets.values()).filter(wallet => wallet.status === status);
  }

  async createWallet(insertWallet: InsertWallet): Promise<Wallet> {
    const id = randomUUID();
    const wallet: Wallet = { 
      ...insertWallet, 
      id, 
      createdAt: new Date(),
      lastActivity: null,
      label: insertWallet.label || null,
      status: insertWallet.status || "idle",
      balance: insertWallet.balance || "0"
    };
    this.wallets.set(id, wallet);
    return wallet;
  }

  async updateWallet(id: string, updates: Partial<Wallet>): Promise<Wallet | undefined> {
    const wallet = this.wallets.get(id);
    if (!wallet) return undefined;

    const updatedWallet = { ...wallet, ...updates };
    this.wallets.set(id, updatedWallet);
    return updatedWallet;
  }

  async deleteWallet(id: string): Promise<boolean> {
    return this.wallets.delete(id);
  }

  // Launch Plan methods
  async getLaunchPlan(id: string): Promise<LaunchPlan | undefined> {
    return this.launchPlans.get(id);
  }

  async getLaunchPlans(): Promise<LaunchPlan[]> {
    return Array.from(this.launchPlans.values()).sort((a, b) => 
      new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
    );
  }

  async createLaunchPlan(insertPlan: InsertLaunchPlan): Promise<LaunchPlan> {
    const id = randomUUID();
    const plan: LaunchPlan = { 
      ...insertPlan, 
      id, 
      createdAt: new Date(),
      status: insertPlan.status || "draft"
    };
    this.launchPlans.set(id, plan);
    return plan;
  }

  async updateLaunchPlan(id: string, updates: Partial<LaunchPlan>): Promise<LaunchPlan | undefined> {
    const plan = this.launchPlans.get(id);
    if (!plan) return undefined;

    const updatedPlan = { ...plan, ...updates };
    this.launchPlans.set(id, updatedPlan);
    return updatedPlan;
  }

  async deleteLaunchPlan(id: string): Promise<boolean> {
    return this.launchPlans.delete(id);
  }

  // Bundle Execution methods
  async getBundleExecution(id: string): Promise<BundleExecution | undefined> {
    return this.bundleExecutions.get(id);
  }

  async getBundleExecutions(): Promise<BundleExecution[]> {
    return Array.from(this.bundleExecutions.values()).sort((a, b) => 
      new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
    );
  }

  async createBundleExecution(insertExecution: InsertBundleExecution): Promise<BundleExecution> {
    const id = randomUUID();
    const execution: BundleExecution = { 
      ...insertExecution, 
      id, 
      createdAt: new Date(),
      startedAt: null,
      completedAt: null,
      status: insertExecution.status || "pending",
      completedWallets: insertExecution.completedWallets || 0,
      failedWallets: insertExecution.failedWallets || 0
    };
    this.bundleExecutions.set(id, execution);
    return execution;
  }

  async updateBundleExecution(id: string, updates: Partial<BundleExecution>): Promise<BundleExecution | undefined> {
    const execution = this.bundleExecutions.get(id);
    if (!execution) return undefined;

    const updatedExecution = { ...execution, ...updates };
    this.bundleExecutions.set(id, updatedExecution);
    return updatedExecution;
  }

  // Activity methods
  async getActivities(limit: number = 50): Promise<Activity[]> {
    return this.activities
      .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
      .slice(0, limit);
  }

  async createActivity(insertActivity: InsertActivity): Promise<Activity> {
    const id = randomUUID();
    const activity: Activity = { 
      ...insertActivity, 
      id, 
      createdAt: new Date(),
      walletId: insertActivity.walletId || null,
      amount: insertActivity.amount || null,
      transactionHash: insertActivity.transactionHash || null
    };
    this.activities.push(activity);
    return activity;
  }

  // System Metrics methods
  async getLatestSystemMetrics(): Promise<SystemMetrics | undefined> {
    return this.systemMetrics.sort((a, b) => 
      new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
    )[0];
  }

  async createSystemMetrics(insertMetrics: InsertSystemMetrics): Promise<SystemMetrics> {
    const id = randomUUID();
    const metrics: SystemMetrics = { 
      ...insertMetrics, 
      id, 
      createdAt: new Date(),
      taxCollected: insertMetrics.taxCollected || "0"
    };
    this.systemMetrics.push(metrics);
    return metrics;
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, decimal, timestamp, integer, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const wallets = pgTable("wallets", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  address: text("address").notNull().unique(),
  privateKey: text("private_key").notNull(),
  publicKey: text("public_key").notNull(),
  balance: decimal("balance", { precision: 18, scale: 8 }).notNull().default("0"),
  status: text("status").notNull().default("idle"), // idle, active, funding, error
  label: text("label"),
  lastActivity: timestamp("last_activity"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const launchPlans = pgTable("launch_plans", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  tokenSymbol: text("token_symbol").notNull(),
  tokenName: text("token_name").notNull(),
  totalSupply: decimal("total_supply", { precision: 18, scale: 0 }).notNull(),
  initialLiquidity: decimal("initial_liquidity", { precision: 18, scale: 8 }).notNull(),
  status: text("status").notNull().default("draft"), // draft, ready, executing, completed, failed
  walletCount: integer("wallet_count").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const bundleExecutions = pgTable("bundle_executions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  launchPlanId: varchar("launch_plan_id").notNull().references(() => launchPlans.id),
  status: text("status").notNull().default("pending"), // pending, executing, completed, failed
  totalWallets: integer("total_wallets").notNull(),
  completedWallets: integer("completed_wallets").notNull().default(0),
  failedWallets: integer("failed_wallets").notNull().default(0),
  startedAt: timestamp("started_at"),
  completedAt: timestamp("completed_at"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const activities = pgTable("activities", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  type: text("type").notNull(), // transfer, wallet_generated, bundle_execution, etc.
  description: text("description").notNull(),
  walletId: varchar("wallet_id").references(() => wallets.id),
  amount: decimal("amount", { precision: 18, scale: 8 }),
  status: text("status").notNull(), // pending, confirmed, failed
  transactionHash: text("transaction_hash"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const systemMetrics = pgTable("system_metrics", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  latency: integer("latency").notNull(), // in milliseconds
  gasPrice: decimal("gas_price", { precision: 10, scale: 2 }).notNull(),
  successRate: decimal("success_rate", { precision: 5, scale: 2 }).notNull(),
  taxCollected: decimal("tax_collected", { precision: 18, scale: 8 }).notNull().default("0"),
  cpuUsage: integer("cpu_usage").notNull(),
  memoryUsage: integer("memory_usage").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Insert schemas
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
});

export const insertWalletSchema = createInsertSchema(wallets).omit({
  id: true,
  createdAt: true,
});

export const insertLaunchPlanSchema = createInsertSchema(launchPlans).omit({
  id: true,
  createdAt: true,
});

export const insertBundleExecutionSchema = createInsertSchema(bundleExecutions).omit({
  id: true,
  createdAt: true,
  startedAt: true,
  completedAt: true,
});

export const insertActivitySchema = createInsertSchema(activities).omit({
  id: true,
  createdAt: true,
});

export const insertSystemMetricsSchema = createInsertSchema(systemMetrics).omit({
  id: true,
  createdAt: true,
});

// Types
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

export type InsertWallet = z.infer<typeof insertWalletSchema>;
export type Wallet = typeof wallets.$inferSelect;

export type InsertLaunchPlan = z.infer<typeof insertLaunchPlanSchema>;
export type LaunchPlan = typeof launchPlans.$inferSelect;

export type InsertBundleExecution = z.infer<typeof insertBundleExecutionSchema>;
export type BundleExecution = typeof bundleExecutions.$inferSelect;

export type InsertActivity = z.infer<typeof insertActivitySchema>;
export type Activity = typeof activities.$inferSelect;

export type InsertSystemMetrics = z.infer<typeof insertSystemMetricsSchema>;
export type SystemMetrics = typeof systemMetrics.$inferSelect;

