simple_backend/src/main.rs.content:
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::io::Read;
use std::sync::{Arc, Mutex};
use tiny_http::{Header, Method, Request, Response, Server};
use uuid::Uuid;

#[derive(Debug, Clone, Serialize, Deserialize)]
struct Wallet {
    id: String,
    address: String,
    private_key: String,
    public_key: String,
    balance: String,
    status: String,
    label: Option<String>,
    last_activity: Option<String>,
    created_at: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
struct Activity {
    id: String,
    #[serde(rename = "type")]
    activity_type: String,
    description: String,
    wallet_id: Option<String>,
    amount: Option<String>,
    status: String,
    transaction_hash: Option<String>,
    created_at: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
struct SystemMetrics {
    id: String,
    latency: u32,
    gas_price: String,
    success_rate: String,
    tax_collected: String,
    cpu_usage: u32,
    memory_usage: u32,
    created_at: String,
}

#[derive(Debug, Serialize, Deserialize)]
struct BulkWalletRequest {
    count: u32,
    initial_balance: String,
    label_prefix: String,
}

#[derive(Debug, Serialize, Deserialize)]
struct Stats {
    total_wallets: usize,
    active_wallets: usize,
    total_balance: String,
}

type Storage = Arc<Mutex<HashMap<String, Wallet>>>;
type ActivityStorage = Arc<Mutex<Vec<Activity>>>;
type MetricsStorage = Arc<Mutex<Option<SystemMetrics>>>;

fn generate_mock_address() -> String {
    format!("0x{:040x}", rand::random::<u128>() as u64)
}

fn generate_mock_key() -> String {
    format!("0x{:064x}", rand::random::<u128>())
}

fn current_timestamp() -> String {
    chrono::Utc::now().to_rfc3339()
}

fn handle_cors(request: &Request<std::io::Empty>) -> Option<Response<std::io::Cursor<Vec<u8>>>> {
    if request.method() == &Method::Options {
        let response = Response::from_string("")
            .with_header(Header::from_bytes(&b"Access-Control-Allow-Origin"[..], &b"*"[..]).unwrap())
            .with_header(Header::from_bytes(&b"Access-Control-Allow-Methods"[..], &b"GET, POST, PUT, DELETE, PATCH, OPTIONS"[..]).unwrap())
            .with_header(Header::from_bytes(&b"Access-Control-Allow-Headers"[..], &b"Content-Type, Authorization"[..]).unwrap())
            .with_header(Header::from_bytes(&b"Access-Control-Max-Age"[..], &b"86400"[..]).unwrap());
        Some(response)
    } else {
        None
    }
}

fn add_cors_headers(response: Response<std::io::Cursor<Vec<u8>>>) -> Response<std::io::Cursor<Vec<u8>>> {
    response
        .with_header(Header::from_bytes(&b"Access-Control-Allow-Origin"[..], &b"*"[..]).unwrap())
        .with_header(Header::from_bytes(&b"Access-Control-Allow-Methods"[..], &b"GET, POST, PUT, DELETE, PATCH, OPTIONS"[..]).unwrap())
        .with_header(Header::from_bytes(&b"Access-Control-Allow-Headers"[..], &b"Content-Type, Authorization"[..]).unwrap())
        .with_header(Header::from_bytes(&b"Content-Type"[..], &b"application/json"[..]).unwrap())
}

fn create_activity(
    activities: &ActivityStorage,
    activity_type: &str,
    description: &str,
    status: &str,
    wallet_id: Option<String>,
    amount: Option<String>,
) {
    let activity = Activity {
        id: Uuid::new_v4().to_string(),
        activity_type: activity_type.to_string(),
        description: description.to_string(),
        wallet_id,
        amount,
        status: status.to_string(),
        transaction_hash: None,
        created_at: current_timestamp(),
    };

    if let Ok(mut activities_guard) = activities.lock() {
        activities_guard.push(activity);
        // Keep only the last 100 activities
        if activities_guard.len() > 100 {
            activities_guard.remove(0);
        }
    }
}

fn update_system_metrics(metrics: &MetricsStorage) {
    let new_metrics = SystemMetrics {
        id: Uuid::new_v4().to_string(),
        latency: 12 + (rand::random::<u32>() % 20),
        gas_price: format!("{:.1}", 5.0 + (rand::random::<f32>() * 2.0)),
        success_rate: format!("{:.1}", 95.0 + (rand::random::<f32>() * 5.0)),
        tax_collected: "0.623".to_string(),
        cpu_usage: 30 + (rand::random::<u32>() % 40),
        memory_usage: 50 + (rand::random::<u32>() % 30),
        created_at: current_timestamp(),
    };

    if let Ok(mut metrics_guard) = metrics.lock() {
        *metrics_guard = Some(new_metrics);
    }
}

fn main() {
    let server = Server::http("0.0.0.0:8000").unwrap();
    println!("Backend server started on http://0.0.0.0:8000");

    let wallets: Storage = Arc::new(Mutex::new(HashMap::new()));
    let activities: ActivityStorage = Arc::new(Mutex::new(Vec::new()));
    let metrics: MetricsStorage = Arc::new(Mutex::new(None));

    // Initialize with some default metrics
    update_system_metrics(&metrics);

    for mut request in server.incoming_requests() {
        let wallets = wallets.clone();
        let activities = activities.clone();
        let metrics = metrics.clone();

        // Handle CORS preflight
        if let Some(cors_response) = handle_cors(&request) {
            let _ = request.respond(cors_response);
            continue;
        }

        let method = request.method().clone();
        let url = request.url().to_string();
        let path_parts: Vec<&str> = url.trim_start_matches('/').split('/').collect();

        let response = match (method, path_parts.as_slice()) {
            // GET /api/wallets
            (Method::Get, ["api", "wallets"]) => {
                if let Ok(wallets_guard) = wallets.lock() {
                    let wallets_vec: Vec<Wallet> = wallets_guard.values().cloned().collect();
                    let json = serde_json::to_string(&wallets_vec).unwrap_or_default();
                    Response::from_string(json)
                } else {
                    Response::from_string(r#"{"error": "Failed to access wallets"}"#).with_status_code(500)
                }
            }

            // POST /api/wallets/bulk
            (Method::Post, ["api", "wallets", "bulk"]) => {
                let mut body = String::new();
                if request.into_reader().read_to_string(&mut body).is_ok() {
                    if let Ok(bulk_request) = serde_json::from_str::<BulkWalletRequest>(&body) {
                        let mut created_wallets = Vec::new();

                        if let Ok(mut wallets_guard) = wallets.lock() {
                            for i in 0..bulk_request.count {
                                let wallet_id = Uuid::new_v4().to_string();
                                let wallet = Wallet {
                                    id: wallet_id.clone(),
                                    address: generate_mock_address(),
                                    private_key: generate_mock_key(),
                                    public_key: generate_mock_key(),
                                    balance: bulk_request.initial_balance.clone(),
                                    status: "idle".to_string(),
                                    label: Some(format!("{} #{:03}", bulk_request.label_prefix, i + 1)),
                                    last_activity: None,
                                    created_at: current_timestamp(),
                                };

                                wallets_guard.insert(wallet_id, wallet.clone());
                                created_wallets.push(wallet);
                            }
                        }

                        // Create activity for bulk generation
                        create_activity(
                            &activities,
                            "bulk_wallet_generation",
                            &format!("Generated {} wallets", bulk_request.count),
                            "confirmed",
                            None,
                            None,
                        );

                        let json = serde_json::to_string(&created_wallets).unwrap_or_default();
                        Response::from_string(json).with_status_code(201)
                    } else {
                        Response::from_string(r#"{"error": "Invalid request body"}"#).with_status_code(400)
                    }
                } else {
                    Response::from_string(r#"{"error": "Failed to read request body"}"#).with_status_code(400)
                }
            }

            // GET /api/activities
            (Method::Get, ["api", "activities"]) => {
                if let Ok(activities_guard) = activities.lock() {
                    let mut activities_vec = activities_guard.clone();
                    activities_vec.reverse(); // Most recent first
                    let json = serde_json::to_string(&activities_vec).unwrap_or_default();
                    Response::from_string(json)
                } else {
                    Response::from_string(r#"{"error": "Failed to access activities"}"#).with_status_code(500)
                }
            }

            // GET /api/system-metrics
            (Method::Get, ["api", "system-metrics"]) => {
                // Update metrics on each request
                update_system_metrics(&metrics);

                if let Ok(metrics_guard) = metrics.lock() {
                    if let Some(ref current_metrics) = *metrics_guard {
                        let json = serde_json::to_string(current_metrics).unwrap_or_default();
                        Response::from_string(json)
                    } else {
                        Response::from_string("null")
                    }
                } else {
                    Response::from_string(r#"{"error": "Failed to access metrics"}"#).with_status_code(500)
                }
            }

            // GET /api/stats
            (Method::Get, ["api", "stats"]) => {
                if let Ok(wallets_guard) = wallets.lock() {
                    let total_wallets = wallets_guard.len();
                    let active_wallets = wallets_guard.values()
                        .filter(|w| w.status == "active")
                        .count();
                    let total_balance: f64 = wallets_guard.values()
                        .map(|w| w.balance.parse::<f64>().unwrap_or(0.0))
                        .sum();

                    let stats = Stats {
                        total_wallets,
                        active_wallets,
                        total_balance: format!("{:.3}", total_balance),
                    };

                    let json = serde_json::to_string(&stats).unwrap_or_default();
                    Response::from_string(json)
                } else {
                    Response::from_string(r#"{"error": "Failed to access wallets"}"#).with_status_code(500)
                }
            }

            _ => {
                Response::from_string(r#"{"error": "Not found"}"#).with_status_code(404)
            }
        };

        let cors_response = add_cors_headers(response);
        let _ = request.respond(cors_response);
    }
}

// Add a simple random number generator since we can't use external crates
mod rand {
    use std::collections::hash_map::DefaultHasher;
    use std::hash::{Hash, Hasher};
    use std::time::{SystemTime, UNIX_EPOCH};

    pub fn random<T>() -> T 
    where 
        T: From<u64>
    {
        let mut hasher = DefaultHasher::new();
        SystemTime::now()
            .duration_since(UNIX_EPOCH)
            .unwrap()
            .as_nanos()
            .hash(&mut hasher);

        T::from(hasher.finish())
    }
}

